import React, { Component } from "react";
import styles from "../styles/Todo.module.scss";

class Todo extends Component {
  constructor() {
    super();
    this.state = {
      text: ""
    };
  }
  onChange = e => {
    this.setState({
      text: e.target.value
    });
  };

  onKeyDown = e => {
    e.keyCode === 13 && this.createTodo();
  };

  createTodo = e => {
    e.preventDefault();
    // this.state.text && this.props.addTodo(this.state.text);
    this.setState({
      text: ""
    });
  };
  render() {
    return (
      <div
        className={styles.todo}
        style={{
          transform: this.props.showTodo
            ? "translateX(0%)"
            : "translateX(-100%)"
        }}
      >
        <div
          className={styles.container}
          style={{
            marginLeft: this.props.showTodo ? "160px" : "25px",
            marginRight: this.props.showTodo ? "40px" : "25px"
          }}
        >
          <div className={styles.panel}>
            <h1>待辦清單</h1>
            <div className={styles.status_panel}>
              <button>未完成</button>
              <button>已完成</button>
            </div>
          </div>
          <form>
            <input
              type="text"
              placeholder="新增待辦事項"
              value={this.state.text}
              onChange={this.onChange}
              onKeyDown={this.onKeyDown}
            />
            <button onSubmit={this.createTodo}>+</button>
          </form>
        </div>
      </div>
    );
  }
}

export default Todo;
