import React, { Component } from "react";
import TodoList from "./TodoList";
import styles from "../styles/Todo.module.scss";

class Todo extends Component {
  constructor() {
    super();
    this.state = {
      text: "",
      todoList: ["000", "111"]
    };
  }
  onChange = e => {
    this.setState({
      text: e.target.value
    });
    console.log(this.state.text);
  };

  onKeyDown = e => {
    e.keyCode === 13 && this.createTodo();
  };

  addTodo = e => {
    e.preventDefault();
    let todos = this.state.todoList;
    let text = this.state.text;
    let id = this.state.todoList.length + 1;
    todos.push({ text: text, completed: false, id: id });
    this.setState({
      todoList: todos
    });
    // console.log(this.state.text);
    // this.state.text && this.props.addTodo(this.state.text);
    // this.setState({
    //   text: ""
    // });
  };

  render() {
    return (
      <div
        className={styles.todo}
        style={{
          transform: this.props.showTodo
            ? "translateX(0%)"
            : "translateX(-100%)"
        }}
      >
        <div
          className={styles.container}
          style={{
            marginLeft: this.props.showTodo ? "160px" : "25px",
            marginRight: this.props.showTodo ? "40px" : "25px"
          }}
        >
          <div className={styles.panel}>
            <h1>待辦清單</h1>
            <div className={styles.status_panel}>
              <button>未完成</button>
              <button>已完成</button>
            </div>
          </div>
          <form>
            <input
              type="text"
              placeholder="新增待辦事項"
              value={this.state.text}
              onChange={this.onChange}
              onKeyDown={this.onKeyDown}
            />
            <button onSubmit={this.addTodo}>+</button>
          </form>
          <TodoList todolist={this.state.todoList} />
        </div>
      </div>
    );
  }
}

export default Todo;
